class ShoppingListViewModel(app: Application) : AndroidViewModel(app), CoroutineScope {

    private val repository: ShoppingRepository
    private val coroutineScope: CoroutineScope = CoroutineScope(Dispatchers.IO)

    private val _shoppingItemsAll = MutableStateFlow<List<ShoppingListItem>>(emptyList())
    val shoppingItemsAll: StateFlow<List<ShoppingListItem>> = _shoppingItemsAll

    private val _shoppingItemsByCategory = MutableStateFlow<Map<Category, List<ShoppingListItem>>>(emptyMap())
    val shoppingItemsByCategory: StateFlow<Map<Category, List<ShoppingListItem>>> = _shoppingItemsByCategory

    init {
        repository = ShoppingRepository(app)
        coroutineScope.launch {
            initAll()
        }
    }

    fun refresh(category: Category) {
        coroutineScope.launch {
            initItems(category)
        }
    }

    private suspend fun initAll() {
        val items = repository.allItems()
        _shoppingItemsAll.value = items
        val itemsByCategory = items.groupBy { it.category }
        _shoppingItemsByCategory.value = itemsByCategory
    }

    private suspend fun initItems(category: Category) {
        val items = repository.itemsByCategory(category)
        val itemsByCategory = mapOf(category to items)
        _shoppingItemsByCategory.value = _shoppingItemsByCategory.value + itemsByCategory
    }

    fun addItem(item: ShoppingListItem) {
        coroutineScope.launch {
            repository.addItem(item)
            refresh(item.category)
        }
    }

    fun deleteItem(id: Int) {
        coroutineScope.launch {
            repository.deleteItem(id)
            refresh(null)
        }
    }

    fun updateItemQuantity(id: Int, quantity: Int) {
        coroutineScope.launch {
            repository.updateItemQuantity(id, quantity)
            refresh(null)
        }
    }
}
